import React, { useEffect, useState } from "react";
import { getUser } from "../api/userApi";
import { useNavigate } from "react-router-dom";

export default function MyPage({ userId }) {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!userId) return;
    getUser(userId).then(setUser).catch(()=>alert("사용자 조회 실패"));
  }, [userId]);

  if (!userId) return <div className="page mypage">로그인 후 이용해 주세요.</div>;
  if (!user) return <div className="page mypage">로딩...</div>;

  return (
    <div className="page mypage">
      <h1>개인 페이지</h1>
      <p>아이디: {user.userId}</p>
      <p>닉네임: {user.nickname}</p>
      <p>이메일: {user.email}</p>
      <div className="mypage-actions">
        <button onClick={() => navigate("/edit-profile")}>개인정보 수정</button>
        <button onClick={() => navigate("/mygroup/1")}>내가 연 모임 보기</button>
      </div>
    </div>
  );
}
